var sections = new Array(parseInt(prompt("How many Sections?")));



function Timer(time) {
    this.time = time * 60; /* how long the timer runs for */
    this.i = 0;
    this.timeObj = {};


}

function drawTimer() {
    var timer = currentTimer;
    $('.circle_animation').css('stroke-dashoffset', '440' - (timer.i * ('440' / timer.time)));
    console.log($('.circle_animation').css('stroke-dashoffset'));

    timer.timeObj.hrs = Math.floor((timer.time - timer.i) / 60 / 60);
    timer.timeObj.min = Math.floor((timer.time - timer.i) / 60) - timer.timeObj.hrs * 60;
    timer.timeObj.sec = (Math.floor((timer.time - timer.i)) - timer.timeObj.min * 60) - timer.timeObj.hrs * 60 * 60;

    timer.timeObj.text = ("Hours: " + timer.timeObj.hrs + ", Minutes: " + timer.timeObj.min + ", Seconds: " + timer.timeObj.sec);

    $('h2.timeleft').text(timer.timeObj.text);
    $('h2.section').text("Current Section: " + (currentSection + 1) + " / " + sections.length);
    $("div.sections").html(sectionvis);

    if (timer.i == timer.time) {
        beep();
        console.log("woo!");
        timer.i = 1;
        currentSection++;
        timer.time = sections[currentSection] * 60;
    }

    if (isNaN(timer.time - timer.i)) {
        setInterval(beep, 200);
    }
    timer.i++;

}

function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}
var sectionvis = "<p>|</p><p>|</p><p>|</p><p>|</p><p>|</p>";
for (let i = 0; i < sections.length; i++) {
    sections[i] = prompt("Length (Minutes) of Section #" + (i + 1) + ":");

}

for (let i = 0; i < sections.length; i++) {
    sectionvis += "<p>Section #" + (i + 1) + " : " + sections[i] + " mins</p>";
}

var currentSection = 0;
var tim = new Timer(sections[currentSection]);

var currentTimer = tim;

drawTimer();
setInterval(drawTimer, 1000);